void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy -iTime*.05;
    uv *= .5*sin(iTime*.1)+1.;

    float level = .3;

    vec3 hdr = texture(iChannel0, uv).rgb;
    vec3 img = hdr;
    hdr = vec3(floor(level+pow(hdr.r, 4.)));

    float it = 70.;
  	float d = 400.;
    float c = it;

    while(it > -1.)
    {
        float texL = texture(iChannel0, uv+vec2(it/d, 0.)).r;
        float texR = texture(iChannel0, uv-vec2(it/d, 0.)).r;
        hdr += floor(level+pow(max(texL,texR), 4.))*(1.-it/c);
        it--;
    }


	fragColor = vec4(img+hdr*.15,1.0);
}
