void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #define t iTime/100.0
    vec4 solidBlack = vec4(0,0.0,0.0,1.0);
    vec2 xy = fragCoord; //We obtain our coordinates for the current pixel
    xy = xy / iResolution.xy - 0.5; //We divide the coordinates by the screen size
    solidBlack.r = mod((xy.x * xy.x + xy.y * xy.y - xy.x * xy.y - t) * 50.0, 1.0);
    solidBlack.g = mod((xy.x * xy.x + xy.y * xy.y + xy.x * xy.y - t) * 50.0, 1.0);
    solidBlack.b = mod((xy.x * xy.x - xy.y * xy.y + xy.x * xy.y + t) * 50.0, 1.0);
    fragColor = solidBlack;
}
