void mainImage( out vec4 f, in vec2 g )
{
    vec2 uv = 4.*(2. * g - iResolution.xy)/iResolution.y;

    g /= 45.;

    float rep = cos(3.14 *( g.y + g.x* sign(sin( 6e3 * length(floor(g)) )) ));

	float a = atan(uv.x, uv.y) / 3.14159 * 1.5;
	float r = length(uv) - iTime*0.5 + rep;
	a+=r * 1.1 ;
	uv = abs(fract(vec2(a+r,a-r))-0.5);

	float x = uv.x*1.248;
	float y = uv.y*6.;
	float z = uv.y*1.8;
	float hex = abs(max(x+y,x) - 1.386);
	vec3 col = hex * 0.6 * vec3(0.15,0.24,0.37)/length(uv);


    f.rgb =  col;
    if (abs(rep) > 0.9)
        f.rgb = col+.5-vec3(.34,1,1)/25./rep;
}
