#define F(x) (log(x)) 

vec3 disk(float radius,vec2 center, vec2 p, vec3 color1, vec3 bgcolor){
float m=smoothstep(radius-0.05,radius+0.05,length(p-center));
vec3 retour=mix(color1,bgcolor,m);
return retour;}

vec3 tourbillon(float radius,float size,vec2 p,vec3 color1,vec3 bgcolor){
    float m=smoothstep(radius-size,radius+size,float(fract(atan(p.y,p.x)*1.909859 + iTime - 4.*F(length(p)))));
    vec3 retour=mix(color1,bgcolor,m);
    retour=mix(retour,color1,m);
     return retour;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius=0.5;
    float size=0.7;
    float radiusDisk=0.2;
    float t = iTime;

    vec2 p = 2.*fragCoord.xy/iResolution.xy-1.;
    vec2 center=vec2(0.0);

    vec3 tex= texture(iChannel0,p).xyz;
    vec3 tex2=texture(iChannel1,p).xyz;
    vec3 tex3=texture(iChannel2,p).xyz;
    tex=tex-vec3(0.7);
    vec3 color1=vec3((1.-length(p))*0.8+abs(0.5*sin(108.6)),(1.-length(p))*0.6+abs(0.5*sin(108.6)),(0.01*length(p))*0.3+abs(0.5*sin(108.6))-0.1*length(p));
    color1=0.03*tex2+0.95*color1+0.025*tex;
    vec3 colorDisk=vec3((1.-length(p))*0.70+abs(sin(108.6)),(1.-length(p))*0.6+abs(sin(108.6)),(length(p))*0.5+abs(sin(108.6)));
    colorDisk=-length(p)*tex3*0.3+0.95*colorDisk;
    vec3 bgcolor=vec3(0.0);
    bgcolor=0.5*tex+0.95*bgcolor;


    vec3 retour=tourbillon(radius,size,p,color1,bgcolor);
    retour=disk(radiusDisk,center,p,colorDisk,retour);
	fragColor = vec4(retour,1.0);
}
