float plot_edge(float d) {
	return smoothstep(3.0 / iResolution.y, 0.0, abs(d));
}

float plot_solid(float d) {
	return smoothstep(3.0 / iResolution.y, 0.0, max(0.0, d));
}

float plot_solid_with_blooming(float d) {
	return 1.0 - clamp(d, 0.0, 1.0);
}

float box(vec2 p) {
	return length(max(abs(p.x),abs(p.y))) - 0.5 * sin(iTime - length(p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float d = box(uv);
    vec3 bg = mix(vec3(0.3, 0.8, 1.0), vec3(1.0, 0.8, 0.6), plot_solid_with_blooming(d));
    bg = mix(bg, vec3(1.0, 0.0, 0.0), plot_solid(d));
    bg = mix(bg, vec3(0.0), plot_edge(d));
    bg -= vec3(0.1) * plot_edge(mod(d + 0.025, 0.05) - 0.025);
    bg -= vec3(0.3) * plot_edge(mod(d + 0.1, 0.2) - 0.1);
    fragColor = vec4(bg, 1.0);
}
