vec2 houseCenter = vec2(0.44,0.4);
vec2 chimneyCenter = vec2(0.45,0.6);
vec2 window1 = vec2(0.53,0.59);
vec2 window2 = vec2 (0.53, 0.46);
vec2 window3 = vec2 (0.45, 0.46);

vec2 houseSize = vec2(0.16,0.25);
vec2 chimneySize = vec2(0.03,0.14);
vec2 windowSize = vec2(0.05,0.05);

float houseSwitch = 1.0;
float chimneySwitch = 1.0;
float windowSwitch = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
    //Background color
	vec4 layer1 = vec4(1.0,0.0,0.0,1.0);
    //house color
    vec4 FG1 = vec4(0.0,0.0,1.0,houseSwitch);
    //chimney color
    vec4 FG2 = vec4(0.0,1.0,0.0,chimneySwitch); 
    //window color
    vec4 layer4 = vec4(1.0,1.0,0.0,windowSwitch);
    vec4 layer5 = vec4(1.0,1.0,0.0,windowSwitch);
    vec4 layer6 = vec4(1.0,1.0,0.0,windowSwitch);
    //builds the house layer
    float interpHouseX = step(houseCenter.x, uv.x) - step(houseCenter.x+houseSize.x,uv.x);
    float interpHouseY = step(houseCenter.y, uv.y) - step(houseCenter.y+houseSize.y,uv.y);
    vec4 layer2 = (interpHouseX * interpHouseY) * FG1;      
  	//builds the chimney layer
    float interpChimneyX = step(chimneyCenter.x, uv.x) - step(chimneyCenter.x+chimneySize.x,uv.x);
    float interpChimneyY = step(chimneyCenter.y, uv.y) - step(chimneyCenter.y+chimneySize.y,uv.y);
    vec4 layer3 = (interpChimneyX * interpChimneyY) * FG2;
    //builds the 3 windows, each one is on a different layer, however all can be shut off with the window switch
    float interpWindow1X = step(window1.x, uv.x) - step(window1.x+windowSize.x,uv.x);
    float interpWindow1Y = step(window1.y, uv.y) - step(window1.y+windowSize.y,uv.y);
    layer4.a *= (interpWindow1X * interpWindow1Y);

    float interpWindow2X = step(window2.x, uv.x) - step(window2.x+windowSize.x,uv.x);
    float interpWindow2Y = step(window2.y, uv.y) - step(window2.y+windowSize.y,uv.y);
    layer5.a *= (interpWindow2X * interpWindow2Y);

    float interpWindow3X = step(window3.x, uv.x) - step(window3.x+windowSize.x,uv.x);
    float interpWindow3Y = step(window3.y, uv.y) - step(window3.y+windowSize.y,uv.y);
    layer6.a *= (interpWindow3X * interpWindow3Y);  
    //mixes the layers
    vec4 combined = mix(layer1,layer3,layer3.a);
    combined = mix(combined, layer2, layer2.a);
    combined = mix(combined, layer4, layer4.a);
    combined = mix(combined, layer5, layer5.a);
    combined = mix(combined, layer6, layer6.a);
    // Output to screen
    fragColor = combined;

}
