vec2 multiply(vec2 a, vec2 b){
    return vec2(       
        a.x * b.x - a.y * b.y,        
        a.x * b.y + a.y * b.x
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord){

    vec2 c = fragCoord/iResolution.xy * vec2(4) - vec2(2);    
    vec2 z = c;
    float count = 0.0;
    float MAX_COUNT = abs(sin(iTime/32.0))*64.0;

    while (length(z) < 2.0 && count < MAX_COUNT) { 
        z = multiply(z,z) + c;
        count++;
    }
    float v = count/MAX_COUNT;  

    fragColor = vec4(v,v,v,1);
}
