vec3 doTunnel( sampler2D sampler, in vec2 uv, in float time ) 
{
    // get polar coordinates    
    float a = atan( uv.y, uv.x );
    float b = atan( uv.y, abs(uv.x) );
    float r = length( uv );

    // pack and animate    
    vec2 uvL = vec2( 1.0/r + time, a/3.14159265359 );
    vec2 uvR = vec2( 1.0/r + time, b/3.14159265359 );

    // fetch from texture    
    return vec4(texture( sampler, uvL ) + vec4(vec2(dFdx(uvR)), vec2(dFdy(uvR)))).xyz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    vec3 col = doTunnel( iChannel0, uv, iTime ) + doTunnel( iChannel1, uv/2.0, -iTime) + doTunnel(iChannel2, uv, sqrt(iTime));
	fragColor = vec4(col,1.0);
}
