vec2 f(vec2 z)
{
    float t = iTime;
    vec2 c = vec2(sin(t / 4.0), cos(t / 4.0) / 20.0);
    return mat2(z, -z.y, z.x)*z + c;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 R = iResolution.xy;
	vec2 z = (fragCoord - .5*R) / R.y;

    for (float i = 0.0; i < 40.0; i += 1.0)
    {
        z = f(z);
    }

    fragColor = vec4(z.y / length(z), z.x / length(z), length(z), 1.0);
}
