// original https://www.shadertoy.com/view/MddSzl# by Andre

float freqs[4];

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv1 = (fragCoord.xy-iResolution.xy*.5) / iResolution.y;
    vec2 uv = uv1*4.0;

    freqs[0] = texture( iChannel1, vec2( 0.01, 0.25 ) ).x;
	freqs[1] = texture( iChannel1, vec2( 0.07, 0.25 ) ).x;
	freqs[2] = texture( iChannel1, vec2( 0.15, 0.25 ) ).x;
	freqs[3] = texture( iChannel1, vec2( 0.30, 0.25 ) ).x;

    float c = 1.;
    uv.x = abs(uv.x);
    uv.x -= 1.;
    float gt = iTime*(1.3+freqs[1]*0.01);
    for (float i = 0.0; i< 100.0; i++) {
      gt += i*.72;
      c *= min(0.1+max(freqs[3]*2.-0.1,1.),distance(uv,
                            vec2(sin(gt*0.9)+cos(gt*(i/37.))
                                ,sin(gt*0.7)+cos(gt*(i/23.)))));
    }

	fragColor = vec4(clamp(
                       mix( vec3(freqs[0]*freqs[0],.1,freqs[2]),
                            vec3(1.0,1.,0.9),
                            smoothstep(.1,0.12,c))
                       *(0.5+texture(iChannel0,vec2(uv1.y*4.,uv.x)+uv1*vec2(7.0,.13)).g
                       *.25*max(-.2,4.0-length(uv)))
                       ,0.0,1.0)
                     ,1.0);
    vec2 position = (fragCoord.xy / iResolution.xy) - vec2(0.5);
    float vignette = smoothstep(0.9, 0.2+freqs[1]/5., length(position));
    fragColor = vec4(fragColor.xyz * vignette, 1.0);


}
