void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius = 100.0 + cos(iTime) * 50.0;
    float centerX = iResolution.x/2.0 + cos(iTime)*250.0;
    float centerY = iResolution.y/2.0 + sin(iTime)*100.0;
    float distance = sqrt( pow(centerX - fragCoord.x, 2.0) + pow(centerY - fragCoord.y, 2.0));
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 vu = 8.0 / iResolution.xy;
    if ( distance < radius){
		fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    }else fragColor = vec4(vu, 0.2+0.8*sin(iTime), 0.5);
}
