void mainImage( out vec4 color, in vec2 pixCoords )
{
	float centerX = 90.0;
    float centerY = 370.0;
    float rayon = 50.0;
    float distance = sqrt(pow(pixCoords.x-centerX,2.0)+pow(pixCoords.y-centerY,2.0));
    float sinus = sin(pixCoords.x / 14.0) * 5.0 + iResolution.y / 3.0;

    color = vec4(0.5, 0.8, 1.0, 1.0);
    if (distance < rayon)
    {
        color = vec4(1.0, 0.9, 0.0, 1.0);
    }
     if (pixCoords.y < sinus)
    {
        color = vec4(0.2, 0.4, 1.0, 1.0);
    }
}
