// short version of https://www.shadertoy.com/view/MtdSz8

void mainImage( out vec4 O,  vec2 U )
{
	vec2 R = iResolution.xy, P;
         U = (U+U-R)/R.y * 12.;

    O -= O;
    if (abs(U.x) > 12.) return;
    float l;

#define S(x,y) P = vec2(x,y); l = length(U-P); if (l < 5.5) U = (U-P) / (3.-l*l/15.) + P
    S( 0, -5);                                                     // bubbles
    S(-6,5.6);
    S( 6,5.6);

    U *= 6.28;


    O += sin(U.x/.87)*sin(U.y/1.5) > 0. ? .5 : .6+.4*cos(U.x/12.); // grey & white faces

    if (abs(l=cos(U.y/1.5))>.5)                                    // black face
        U = sin( U * mat2(.575,1,.575,-1) ),
        U.x*U.y*l > 0. ? O-=O : O;

}
