void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 movie = texture(iChannel1,uv);
    vec4 img = texture(iChannel0, uv);
    if (movie.g + movie.b + movie.r < 1.0) {
    	fragColor = vec4(0.0 - movie.g/3.0,0.0 - movie.g/3.0,0.0 - movie.g/3.0,0.0);
    }
    else if (movie.g + movie.b + movie.r < 1.1) {
    	fragColor = vec4(0.2 - movie.g/3.0,0.2 - movie.g/3.0,0.2 - movie.g/3.0,0.1);
    }
    else {
		fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    }
}
