mat2 rotationMatrix(float theta)
{
    return mat2(
        cos(theta),-sin(theta),
        sin(theta),cos(theta)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 originalUV = uv;
    uv.x *= iResolution.x / iResolution.y;
    uv = fract(uv * 4.) * 2. - 1.;

    for(int i = 0; i < 6; i++)
        uv *= rotationMatrix(acos(dot(uv,uv)) + iTime * float(i));

    vec3 col = vec3(uv,distance(uv,originalUV));


    fragColor = vec4(col,1.0);
}
