void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

   float circleNumber = 3.0;
   float circleEdgeSharpness = 8.0;
   float motionSpeed = -1.0;
   vec4 colorMask = vec4(1.0, 0.5, 0.1, 1.0);

   float distanceToCenter = distance(uv, vec2(0.0, 0.0)) * circleNumber;

   float signedDistance = sin(5.0 * distanceToCenter + iTime * motionSpeed) * circleEdgeSharpness;

   signedDistance = clamp(signedDistance, 0.0, 1.0);

   fragColor = vec4(vec3(signedDistance), 1.0);

   fragColor *= colorMask;
}
