#define rgb(r,g,b) (vec4(r,g,b,255.)/255.)
#define saturate(x) (clamp(0.,1.,x))

float rand(float x) { return clamp(0.,1.,fract(sin(x* 71523.5413291))); }
float rand(vec2 x) { return rand(dot(x, vec2(13.4251, 15.5128))); }

float noise(vec2 x)
{
    vec2 i = floor(x);
    vec2 f = x - i;
    f *= f*(3.-2.*f);
    return mix(mix(rand(i), rand(i+vec2(1,0)), f.x),
               mix(rand(i+vec2(0,1)), rand(i+vec2(1,1)), f.x), f.y);
}

float fbm(vec2 x)
{
    float r = 0.0, s = 1.0, w = 1.0;
    for (int i=0; i<5; i++)
    {
        s *= 2.0;
        w *= 0.5;
        r += w * noise(s * x);
    }
    return r;
}

float cloud(vec2 uv, float scalex, float scaley, float density, float sharpness, float speed)
{
    return pow(saturate(fbm(vec2(scalex,scaley)*(uv+vec2(speed,0)*iTime))-(1.0-density)), 1.0-sharpness);
}

float grass(vec2 uv, float seed){
    float x = clamp(0.,0.5, seed);
    float y = clamp(0., 1., seed)-0.5;
    float period = clamp(0.,1., seed)*1.;
    float amp = clamp(.2,.22, seed);
    float blade = abs(uv.y-y-(amp*sin((uv.x+iTime)*period)+0.5));
    float thickness = clamp(0.1,0.3,seed)*0.04;
    float len = (seed*0.5);

	return smoothstep(0.,thickness*smoothstep(0.1,0.1+len,sin(uv.x+(len-0.5)/2.)), blade);   
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 color = texture(iChannel0, uv);
    color /= 5.;
	//vec4 color = vec4(uv/2.,1.0,1.0)*0.2;

    //vec4 color = vec4(0.01);

    vec4 grassColor = rgb(25,75,42);

    for(float i = 0.; i <= 20.; i++) {
        float offset = rand(i);
        grassColor.g += (offset-0.5)*0.1;
        grassColor.b += (offset-0.2)*0.01;

    	color = mix(grassColor, color, grass(uv, offset));
    }

    //color = vec4(0.01);
    // clouds
    vec4 cl1 = mix(rgb(131,158,210), rgb(166,191,224), uv.y-sin(iTime/4.));
    float d1 = mix(0.9,0.1,pow(uv.y, 0.7));

    color = mix(color, cl1, cloud(uv,2.,8.,d1,0.4,0.04));

    //color = mix(color, vec4(0.2,0.2,0.2,1.), 5.*cloud(uv,12.,15.,0.9,0.75,0.03) * cloud(uv,2.,8.,0.5,0.0,0.02)*uv.y);
    fragColor = color;
}
