void mainImage (out vec4 o, in vec2 v) {
    float t = iTime;
    float z = iResolution.x*(0.1+0.03*cos(t));
    float a = 0.1*t;
    float s = sin(a);
    float c = cos(a);
    v = mat2(c,-s,s,c)*((v/z)-vec2(3.*cos(t*0.1)+0.05*t,3.*sin(t*0.2)));

    float r = fract(cos(dot(floor(v),vec2(12.9898,78.233)))*43758.5453); 
    float d = distance(fract(v),vec2(0.5))/((0.3+0.2*r)*(0.8+0.3*cos(6.*r*t)));

    if (d < 1.) {
        o = vec4(0.5+0.5*r,cos(20.*d),0.5+0.5*cos(r*99.),1.);
    } else {
        float b = 0.7+0.3*cos(2.*v.y+5.*t);
    	o = vec4(1.,0.5+0.5*b,b*(0.6+0.4*cos(25.*v.x)),1.);
    }
}

