void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

vec4 cell(vec2 fragCoord, vec2 pixel)
{
	vec2 uv = (fragCoord - pixel) / iResolution.xy;
    return abs(texture(iChannel0, uv));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float ti = sin(iTime * 5.);

    float frame = float(iFrame);
    float frameInterval = 1000.;

    if (frame < 200.)//mod(frame, frameInterval) < frameInterval * .5) // init buffer with iChannel2 texture
    {
        fragColor = texture(iChannel1, uv);
    }
    else
    {
        // get current cell from backbuffer
        vec4 c = cell(fragCoord, vec2(0,0)); 	// current cell

        // get adjacents cells from backbuffer 
        vec4 l = cell(fragCoord, vec2(-1,0)); 	// left cell
        vec4 r = cell(fragCoord, vec2(1,0)); 	// rigt cell
        vec4 t = cell(fragCoord, vec2(0,1)); 	// top cell
        vec4 b = cell(fragCoord, vec2(0,-1)); 	// bottom cell    
        vec4 lt = cell(fragCoord, vec2(-1,1)); 	// top left cell
        vec4 rt = cell(fragCoord, vec2(1,1)); 	// top rigt cell
        vec4 lb = cell(fragCoord, vec2(-1,-1)); 	// bottom left cell
        vec4 rb = cell(fragCoord, vec2(1,-1)); 	// bottom right cell    

        // set buffer
        fragColor = (l+r+t+b+lt+rt+lb+rb)/8.;       
	}
}
