#define maxHeight 		(1024)
#define histogramSmooth (1.5/iResolution.y)
#define histogramBlack  (1.)
#define min3(a,b,c)     (min( (a) , min( (b) , (c) ) ))
#define minvec3(x)     (min3( (x).r , (x).g , (x).b ))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0,uv);

    // mean computing
    vec3 mean = vec3(0.);
    float H = iChannelResolution[0].y;
    for( int i = 0 ; i < maxHeight ; i++ ){
        if( float(i) <= H ){
            mean += texture(iChannel0,vec2(uv.x,float(i)/H)).rgb;
        }
    }

    // add histogram
    vec3 bars = mean/(3.*H) - vec3(0.,-1./3.,-2./3.);
    bars = (1. - abs( (bars - uv.y) / histogramSmooth ));
    fragColor.rgb += clamp(bars.rgb,0.,1.);

    float black = (1. - minvec3( abs(bars) )/histogramBlack);
    fragColor *= 1. - clamp( black , 0. , 1. );

}
