#define threshold 0.55

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 greenScreen = vec4(0.,1.,0.,1.);
    vec4 color = texture(iChannel0, uv);

    vec3 diff = color.xyz - greenScreen.xyz;
            vec4 freqs;
     	freqs.x = texture( iChannel1, vec2( 0.01, 0.25 ) ).x;
        freqs.y = texture( iChannel1, vec2( 0.07, 0.25 ) ).x;
        freqs.z = texture( iChannel1, vec2( 0.15, 0.25 ) ).x;
        freqs.w = texture( iChannel1, vec2( 0.30, 0.25 ) ).x;
    if(dot(diff, diff) < threshold) {
        color = freqs;

    }else{
    	color = color;
    }

	fragColor = color;
}
