float f (in vec3 p)
{
    p.z -= iTime;
    return length(cos(p+p.z-p.x+iTime)+.5*sin(p.x+p.y*.1-iTime*.5)) - clamp(1. * abs(sin(iTime *.7)), .7, 1.);
}

void mainImage( out vec4 c, in vec2 u )
{
    vec3 p = .5-vec3(u, 1.)/iResolution;
    p.y += .3;
    p.x = -abs(p.x);
    vec3 o = p;
    for (int i = 0; i < 200; i++)
    {
        o += f(o)*p;
    }
	c = vec4(vec3(1.) * f(o-p) * vec3(f(o-p)*cos(iTime+o.x), f(o-p), f(o-p) * cos(p.x + p.y + o.x)),1.);
}
