#define MinRadius 0.25
#define MaxRadius 0.50
vec3 green = vec3(0.0,1.0,0.0);
vec3 white = vec3(1.0,1.0,1.0);
vec3 brown = vec3(210.0,105.0,30.0)/255.0;
vec3 saffron = vec3(255., 153., 51.)/255.;

vec4 ract(vec2 uv,vec3 color){
    if(uv.x < 0.8 && uv.x > 0.2 && uv.y < 0.8 && uv.y > 0.2 )
        return vec4(color,1.0);
    else
        return vec4(color,0.0);
}
vec4 ract(vec2 uv,vec2 center ,float width,float height,
          vec3 color){

    float xMin = center.x - width*0.5;
    float xMax = center.x + width*0.5;
    float yMin = center.y - height*0.5;
    float yMax = center.y + height*0.5;


    if(uv.x < xMax && uv.x > xMin && uv.y < yMax && uv.y > yMin )
        return vec4(color,1.0);
    else
        return vec4(0.0);
}
void ring(vec2 uv){
      float radius = MinRadius +(MaxRadius-MinRadius);

}
#define S(v) smoothstep( 2./R.y, -2./R.y, v )
#define B(v,e) S( abs(v) - e )


vec4 chakra(vec2 u)    {  

    vec2 u1 = u*8.0;

    u1.x = u1.x - 3.50*iResolution.x;
    u1.y = u1.y - 3.50*iResolution.y;
	vec2  R = iResolution.xy;
    vec2  U = 2.0*u1   -     R;                              
    float l = length(U)/R.y *1.2,   d=.131,       
          a = mod( atan(U.y,U.x) +d, d+d ) - d;   
     vec4 c=vec4(1.0);
    c = c   -B(l,.1) -1.4*B(l,.22) + B(l,.26) + B(l-1.,.07)        \
          + B(a*l,.03)*S(l-1.1)+ B((a+d)*l,.03)*S(abs(l-.92)-.02) \
          + B(l-1.12,.02)*S(abs(a)-.05) - 1.;
    return c;        

 }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy/iResolution.xy;
    float ratioFactor =   iResolution.x/iResolution.y;
    uv.x *= ratioFactor;

    vec2 center  = vec2(0.5*iResolution.x/iResolution.y,0.5);


   vec4 c = chakra(fragCoord  );
   vec4 circle = vec4(0.0,0.0,1.0,c);      



    vec4 backGround = vec4(135.,206.,250.,255.0)/255.;
    vec4 layer1 = ract(uv,vec2(center.x,center.y-0.1),0.4,0.1,green);
    vec4 layer2 = ract(uv,center,0.4,0.1,white);
    vec4 layer3 = ract(uv,vec2(center.x,center.y+0.1),0.4,0.1,saffron);
    vec4 layer4 = ract(uv,vec2(center.x-0.2-0.01,center.y-0.15),0.02,0.6,brown);

    vec4 final = layer1 + layer2+ layer3+layer4 ;

    final=mix(final,circle,circle.a);
    fragColor = mix(backGround,final,final.a) ;
}
