void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 newColor = texture(iChannel0, uv);

    float increasedBrightness = 0.1;

    //increase brightness by ten percent (multiply by current brightness plus extra)
    newColor *= (1.0 + increasedBrightness);
    newColor += vec4(0.0, 0.0, 0.0, 0.0);

    if (newColor.r >= 0.6 && newColor.g >= 0.6 && newColor.b >= 0.6)
    {
       newColor.r += 1.0;
       newColor.b -= 0.5;
       newColor.g -= 0.5;
    }
    else if ((newColor.r >= 0.4 && newColor.r < 0.6) && (newColor.g >= 0.4 && newColor.g < 0.6) && (newColor.b >= 0.4 && newColor.b < 0.6))
    {
       newColor.g += 0.5;   
       newColor.b -= 0.1;
       newColor.r += 0.3;

    }
    else if ((newColor.r >= 0.3 && newColor.r < 0.4) && (newColor.g >= 0.3 && newColor.g < 0.4) && (newColor.b >= 0.3 && newColor.b < 0.4))
    {
       newColor.g += 0.6;   
    }
    else
    {
        newColor.b += 1.2;
    }

    // Output to screen
    fragColor = newColor;
}
