#define S(d) smoothstep(4./R.y,0., d)

vec2 polarRep(vec2 U, float n) {
    n = 6.283/n;
    float a = atan(U.y, U.x),
          r = length(U);
    a = mod(a+n/2.,n) - n/2.;
    U = r * vec2(cos(a), sin(a));
    return .5* ( U+U - vec2(1,0) );
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U = ( U+U - R ) / R.y;

    float t = iTime/5.,
          n = 10.* (.5-.5*cos(6.28*t));

    for( float i=0.; i < mod(t,4.); i++)
        U = polarRep(U, n);

    O = texture(iChannel0, .5+U);
}
