const vec3 RED = vec3(1.0, 0.0, 0.0);
const vec3 GREEN = vec3(0.0, 1.0, 0.0);
const vec3 BLUE = vec3(0.0, 0.0, 1.0);
const vec3 PURPLE = vec3(1.0, 0.0, 1.0);
const vec3 CYAN = vec3(0.0, 1.0, 1.0);

// rate = long radius / short radius
vec3 DrawEllipse(in vec2 uv, in vec2 center, float radius, float rate, float blur, in vec3 backCol, in vec3 drawCol)
{
    vec2 origin = uv - center;
    origin.y /= rate;
    float dis = length(origin);

    float t = smoothstep(radius, radius - blur, dis);
    vec3 col = mix(backCol, drawCol, t);
    return col;
}

vec3 DrawCircle(in vec2 uv, in vec2 center, float radius, float blur, in vec3 backCol, in vec3 drawCol)
{
    float dis = length(uv - center);
    float rate = smoothstep(radius, radius - blur, dis);
    vec3 col = mix(backCol, drawCol, rate);
    return col;
}

vec3 DrawMouse(in vec2 uv, in vec2 center,in float offset, float radius, float blur, in vec3 backCol, in vec3 drawCol)
{
    float dis = length(uv - center);
    float rate1 = smoothstep(radius, radius - blur, dis);
    float dis2 = length(uv - vec2(center.x, center.y + offset));
    float rate2 = smoothstep(radius, radius - blur, dis2);
    float rate = rate1 - rate2;
    vec3 col = mix(backCol, drawCol, rate);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv *= 2.0;  //(x,y -> [-1,1])
    uv.x *= iResolution.x / iResolution.y; //x unit equal y unit
	// head
    vec3 head = DrawCircle(uv, vec2(0.0,0.0), 0.8, 0.1, RED, BLUE);
    // left eye  
    vec3 leftEye = DrawEllipse(uv, vec2(-0.35,0.2), 0.12, 0.6, 0.02, head, GREEN);
    // right eye
    vec3 rightEye = DrawEllipse(uv, vec2(0.35,0.2), 0.12, 0.6, 0.02, leftEye, GREEN);
    // nose
    vec3 nose = DrawEllipse(uv, vec2(0.0,-0.08), 0.04, 1.0, 0.02, rightEye, CYAN);

    // mouth
    float offset = sin(iTime * 5.0) * 0.04;
    vec3 mouth;
    if(offset < 0.0)
    {
        mouth = DrawMouse(uv, vec2(0.0,-0.5), offset, 0.1, 0.02, nose, PURPLE);
    }
    else
    {
        mouth = DrawMouse(uv, vec2(0.0,-0.4), offset, 0.1, 0.02, nose, PURPLE);
    }

    vec3 lastCol = mouth;
    // Output to screen
    fragColor = vec4(lastCol,1.0);
}
