#define pi 3.14159265359
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv += min(max(iTime-20.0, 0.0), 0.1*cos(0.3*iTime))*cos(iTime*uv.x)*sin(iTime*uv.y);
    vec2 p = uv;

    p.x -= 0.5;
    p.y -=0.5;
    p.y /= iResolution.x/iResolution.y;


    float pStrength = min(max(iTime-5.0, 0.), 1.);
    p.x += pStrength*0.2*cos(iTime);
    p.y += pStrength*0.1*sin(4.*iTime);

    float angle = 0.;
        angle = atan(p.x/p.y);
    if (p.y > 0.)
        angle = angle + pi;
	angle += iTime;   

    float dStrength = 0.5*abs(sin(max(iTime-10.0, 0.)));
	float d = (.5+dStrength*(sin(iTime)*cos(angle)+cos(iTime)*sin(4.*angle)))/dot(p, p);
    float d2 = d-10.0*iTime;
    d+= 10.0*iTime;
    if(iTime<13.)
        d2 = d;

    angle += dStrength*cos(0.3*d)*cos(0.7*iTime);

    vec3 col = vec3(0.);
    float aDiv = 2.0;
    col = texture(iChannel0, vec2(mod(d,10.0)/10.0, mod(angle, pi/aDiv)/(pi/aDiv))).rgb;
    col += 0.5*texture(iChannel1, vec2(mod(d2,10.0)/10.0, mod(5.*angle+cos(iTime), pi/aDiv)/(pi/aDiv))).rgb;
    // Output to screen
    fragColor = vec4(col,1.0);
}
