vec2 toUv(vec2 p) {
	vec2 uv = ((p / iResolution.xy) * vec2(1.,1.)) + vec2(-0.5,-0.5);
	uv.x *= iResolution.x/iResolution.y;

    // uv.x = [roughly -0.5, roughly 0.5]
	// uv.y = [-0.5, 0.5]
	return uv;
}

float sineBounded(float x, float miny, float maxy) {
	return ((sin(x) + 1.0)/2.)*(maxy - miny) + miny;
}

vec3 circle(vec2 center, float minRadius, float maxRadius, float speed, vec2 fragCoord) {
	vec2 frag = toUv(fragCoord.xy);
	float radius = sineBounded(iTime*speed, minRadius, maxRadius);

	float scale = distance(frag,center);
	if (scale <= radius) {
		scale = inversesqrt(scale);
	}

	vec3 from = vec3(0.2,0.2,0.5);
	vec3 to = vec3(1.0,0.4,0.8);
	return ((to - from) * scale) + from;
}


vec2 orbit(vec2 center, float len) {
	return center + vec2(cos(iTime)*len, sin(iTime)*len);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float radius = 0.2;
	vec2 p1 = vec2(sin(iTime*0.2)*0.5,cos(iTime*0.2)*0.2);
	vec3 c1 = circle(p1, radius,radius, 1., fragCoord);
	fragColor = vec4(c1, 1.0);

	for (int i = 0; i < 4; i++) {
		vec2 p2 = orbit(p1, radius);
		radius *= pow(sin(iTime)*3.0,0.9);
		vec3 c2 = circle(p2, radius,radius,1., fragCoord);
		p1 = p2;
		fragColor = mix(vec4(c2, 1.0), fragColor, vec4(0.5,0.5,0.5,1.0));
	}


	{
		vec2 p1 = vec2(sin(iTime*0.2)*0.5,cos(iTime*0.2)*0.2);
		vec2 p2 = orbit(p1, 0.32);
		vec2 p3 = vec2(0.5,0.5);
		vec2 p4 = orbit(p2, 0.02);
		vec3 c1 = circle(p1, 0.2, 0.2, 1., fragCoord);
		vec3 c2 = circle(p2, 0.05, 0.05,1., fragCoord);
		vec3 c3 = circle(p3, 0.2, 0.4,1., fragCoord);
		vec3 c4 = circle(p4, 0.01, 0.025,50., fragCoord);

		vec4 frag2 = vec4(clamp(c1*c2*c3*c4, 0.23,0.95), 1.0);
		fragColor = mix(frag2, fragColor, vec4(0.5,0.5,0.5,1.0));
	}
}
