#define PI	3.14159265359

vec2 rotate(vec2 p, float t)
{
	return p*cos(t)+vec2(p.y, -p.x)*sin(t);
}

float de(vec2 p)
{
	float r = length(p);
	float a = atan(p.y, p.x);
	return 1.8*r-(abs(mod(5.0*a/PI,2.0)-1.0)+0.8);	
}

float map(vec2 p)
{	
	vec2 e = vec2(0.05, 0.0);
	// https://iquilezles.org/articles/distance
	vec2 g = vec2(de(p+e.xy)-de(p-e.xy),de(p+e.yx)-de(p-e.yx))/(2.0*e.x);
    float d = abs(de(p))/length(g);
	return smoothstep(0.00, 0.05, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (fragCoord.xy*2.0-iResolution.xy)/iResolution.y;
	p = mod(p*5.0, 2.0)-1.0;
	p = rotate(p, iTime*0.2);
	fragColor = vec4(vec3(1.0, 0.5, 0.5)*map(p), 1.0);
}
