// [SH17A] Floor B9 redux
// By David Hoskins.

void mainImage( out vec4 o, in vec2 c)
{
    // Camera and step stuff...
    vec4 d=vec4(c / iResolution.xy, 0,.1) - .5, p = d + vec4(sin(iTime * .2) * 4., -abs(sin(iTime * 6.1)) * .5, iTime * 7., 0),  t;
    d.x += p.x * .03;			// ...Camera left/right look
    for(int i = 0; i++ < 150;) 	// ...WebGL 2 loop
    	t = mod(p, 18.) - 9., 	// ...Repeat
        o = texture(iChannel0, abs(t.zxy)-7.) * 20. / p.w,		//... Divide by distance for darkness
		p -= d * min(abs(t.y), length(t.xz) - 2.5 + p.y * .1);	// ...I don't need to escape the loop, ever.
}
