/*
	Due to the high effort of you lovely guys I've made a comparison.

	You can change which one is viewed moving the mouse (while clicked) in the 4 quadrants.

	 ________ ________ 
	|        |        |
	|        |        |
	|   iq   | Fabrice|
	|________|________|
	|        |        |
	|        |        |
	|  orig. | coyote |
	|________|________|

	( I've put the last one of each one )

*/
void mainImage( out vec4 o, vec2 c )
{
    vec2 uv = c / iResolution.xy;
    vec2 uvm = iMouse.xy / iResolution.xy;
    if ( uvm.x < 0.5 && uvm.y < 0.5 )
    {
    	o = texture( iChannel0, uv );
    } else
    if ( uvm.x < 0.5 && uvm.y >= 0.5 )
    {
    	o = texture( iChannel1, uv );
    } else
    if ( uvm.x >= 0.5 && uvm.y >= 0.5 )
    {
    	o = texture( iChannel2, uv );
    } else
    if ( uvm.x >= 0.5 && uvm.y < 0.5 )
    {
    	o = texture( iChannel3, uv );
    }
}

// Original code

void mainImage( out vec4 o, vec2 c )
{
    //vec4 V = vec4(49,170,225,1)/255.;
    //vec4 V = vec4(29,161,242,1)/255.;
    vec4 V = vec4(.192,.667,.882,1),Z=vec4(0),b=Z;
    float A=18.,B=32.,C=15.,D=20.,E=22.,F=5.5;
    #define K(o,X,Y,r,T,B,E) o = length( c - vec2(X,Y) ) < r B ? T : E;
    #define P(o,X,Y,r) K(o,X,Y,r,V,,o)
    #define A(o,X,Y,r) K(o,X,Y,r,V,&&o.x>0.,Z)
    #define M(o,X,Y,r) K(o,X,Y,r,Z,,o)
    //#define U o = o.x + b.x != 0. ? V : Z; b=Z;
    //#define U o += b;

    c *= .1;

    P(b,B,D,C)
    P(o,B,D,C)
    M(o,40,B,C)
    M(o,E,E,C)
    A(b,42,E,C)
    M(b,38,36,A)
    P(o,41.5,D, F)

    //U
    P(b,B,C, F)
    M(b,28,A, F)

    P(b,31,A, F)
    M(b,28,E, F)

    P(b,31,21, F)
    M(b,38,36,A)

    // U
    o += b;

    P(b,38,36,A)
    M(b,46,37.5,C)
    A(b,48,D, F)

    //U

    P(b,38,35,C)
    M(b,43,42,A)
    A(b,47,E,F)

    //U
    o = (1.-step(0.,-o - b))*V;
}


