void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 uv2 = uv.xy + vec2(1.0 / 256.0, 1.0 / 256.0);
	float d = .00375;
    //sampler2D sampler_blur = iChannel0;

	vec3 val = texture(iChannel0, vec2(uv2.x - d, uv2.y - d)).xyz;

	val += texture(iChannel0, vec2(uv2.x + d, uv2.y - d)).xyz;
	val += texture(iChannel0, vec2(uv2.x + d, uv2.y + d)).xyz;
	val += texture(iChannel0, vec2(uv2.x - d, uv2.y - d)).xyz;

	vec3 val2 = texture(iChannel0, vec2(uv2.x - d, uv2.y - d)).xyz;

	val2 += texture(iChannel0, vec2(uv2.x - d, uv2.y + d)).xyz;
	val2 += texture(iChannel0, vec2(uv2.x + d, uv2.y + d)).xyz;
	val2 += texture(iChannel0, vec2(uv2.x - d, uv2.y - d)).xyz;

	val *= 0.65;
	val2 *= 0.35;

	fragColor.xyz = val * 0.25 + val2 * 0.25;
	fragColor.w = 1.0;
}
