void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
	vec2 uv = (2.0*fragCoord.xy-iResolution.xy) / iResolution.x;

    vec2 c = uv;

    c.x += sin(time/5.0)/4.0;
    c.y += cos(time/3.0)/4.0;

    vec2 p = vec2(0,0);
    p.y = 1.0/length(c*4.0);
    float angle = atan(c.x,c.y);
    p.x = angle/4.0;
    p.y += time/8.0;
    vec4 color = mix(texture(iChannel0,p),texture(iChannel1,p),0.3);

  	vec4 water = vec4(1.0,0.5,0.0,1.0)*0.1;
    color = mix(color,water,smoothstep(0.0,0.08,sin(p.y*8.0+time*3.0)*0.02+(abs(angle)-2.0)));
    color *= min(1.0,(c.x*c.x+c.y*c.y));
	fragColor = color;
}
