#define time iTime
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);
	float ratio = iResolution.x / 18.;
    //float ratio = 500. / iResolution.x;
    float x = (sin(uv.x*ratio)-.85)*20.;
    float y = (cos(uv.y*ratio)-.85)*20.;

    float g = max(x,y) / 7.;

    //Because today I forgot how to make circles..
    //Thought it was something like this. 
    float t = 6.; 
    float r = sqrt((x-t)*(x-t) + (y-t)*(y-t));

    //Circle square thing
    float c = 1.0 - smoothstep(.8, 1., r / 9.);

    //I'm tired
    col = vec3(step(1.2 - g, 1.), 0., 0.)  + c;

	fragColor = vec4(col, 1.0);
}
