vec4 draw_simple_star(float inner_rad, vec4 color, vec2 fragCoord) {
	// Distance from the center of the image to the border, in [0, 1]
    vec2 R = iResolution.xy; 
    vec2 uv = (fragCoord -.5 * R) / R.x;
    float dist = length (uv) * 2.;

    float col = pow(smoothstep(0.5, 0.2, dist), 2.0) * 10.0;
    float glow = smoothstep(1.0, 0.0, dist) * 0.9;
    return vec4(color.rgb * max(col, glow), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	fragColor = draw_simple_star(0.1, vec4(0.1, 0.8, 0.99, 1.0), fragCoord);
}
