#define PI 3.14159265
#define PULSE(T, x) sin(1. / (T) * 2. * PI * (x))
#define TIME_PULSE(T) PULSE((T), iTime)

float fixup(float x) {
    return x >= 0. ? x : 1. / x;
}

float effect(vec2 uv) {
    float r = length(uv);
    float alpha = atan(uv.y, uv.x);
    float P = 15.;
    //return 1. * (.8 + 0.5 * sin(5. * alpha + (1. + P * TIME_PULSE(10.)) * 2. * PI * r));
    return 1. +  (tanh((r - 0.05) * 100.) + 1.) * fixup(0.5 * (1. - tanh((r - 0.1) * 2. * PI)))
        * (0.1 * sin(5. * alpha + (1. + P * TIME_PULSE(10.)) * 2. * PI * r));
}

mat2 rot2d(float z) {
    float c = cos(z);
    float s = sin(z);
    return mat2( c, s,
                -s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy - vec2(.5);

    // TODO: use modulo instead of loop
        int n = 3;
    for (int i = 0; i < n; i++) {
        mat2 tr = rot2d((iTime / 50. + float(i) / float(n)) * 2. * PI);
        uv *= effect(uv - vec2(0.25) * tr);
    }

	fragColor = texture(iChannel0, vec2(.5) + uv);
}
