const float PI = 3.14159;
const float PERMITTIVITYOFFREESPACE = 8.85419 * pow(10.0, -12.0);
const float LIGHTSPEED = 2.99792 * pow(10.0, 8.0);


float SingleSlitIntensity(float _E, float _b)
{
	float _I0; 
    _I0 = 2.0 * PERMITTIVITYOFFREESPACE * LIGHTSPEED * pow(_E, 2.0);
    return _I0 * pow(sin(_b / 2.0) / (_b / 2.0), 2.0);
}

float Beta(float _a, float _R, float _l, float x)
{
    return (PI * _a * x) / (_l * _R);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
   	vec2 uv = fragCoord;
    uv.x = uv.x - iResolution.x / 2.0;
    uv.y = uv.y - iResolution.y / 2.0; 

    vec4 _lightColor = vec4(0.0, 1.0, 0.0, 1.0);

    float _a = 0.000000047;					//Width of slits in meters.
    float _l = 633.0 * pow(10.0, -9.0);		//WaveLength (nm).
    float _R = 8.0;							//Distance between slits and screen.
    float _E = 11.0;						//Efield amplitude.

    float _b = Beta(_a, _R, _l, uv.x);			//Beta
    float _I = SingleSlitIntensity(_E, _b);	//Intensity


	fragColor = _lightColor * _I;
}
