void mainImage( out vec4 p, vec2 x ) {

    vec2 o = iResolution.xy, d = (x+x - o)/o.y/.9;
    float t=iTime, s=5.+2.*sin(t), w = t-d.y*s;

    o = d;
    d.x += s = sin(w)/s;
    s = 1. - dot(d, d);
    d.y -= d.x * cos(w);

    vec3 n = s < 0. ? vec3(o, 2) : vec3(d, sqrt(s));

    // should work, according to https://stackoverflow.com/a/28577878
    n.xz *= mat2( w = cos(t*=.1), t = sin(t), -t, w);

    p = texture(iChannel0, n) + (s < 0. ? 0. : .5*n.y);

}
