//Draw Circle Function(position, radius, sharpness)
float Circle(vec2 uv, float r, float blur)
	{
    	float d = length(uv);
        float c = smoothstep(r, r-blur, d);

        return c;

    }

//OUTPUT

void mainImage( out vec4 fragColor, in vec2 fragCoord )    
{

    //AudioChannel Stuff

float audioBASS = texture(iChannel0, vec2(-0.2) ).x;

float audioTREBLE = texture(iChannel0, vec2(0.5) ).x;



    vec2 uv = fragCoord.xy / iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    //color
    vec3 color1 = vec3(0.0);
    vec3 color2 = vec3(0.0);

    float circle1 = Circle(uv, audioBASS*0.4, 0.03);

    float circle2 = Circle(uv, audioTREBLE*0.35, 0.04);

    circle1 -= circle2;

   color1 = vec3(circle2*2.0, 0.5 ,0.0)*circle1;
   color2 = vec3(0.0,0.0,1.0)*circle2;

	//fragColor = vec4(vec3(c1), 1.0);
    fragColor = vec4(color1*2.0, 1);
}
