void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixel = fragCoord.xy;
    pixel.x = pixel.x / iResolution.x;
    pixel.y = pixel.y / iResolution.y;



    vec4 solidRed = vec4(0,0.0,0.0,1.0);

    solidRed.r = pixel.x; // L ( black to red )
    solidRed.g = pixel.y; // T ( green to black )
    solidRed.b =  1.0 + (-1.0 * pixel.x); // R ( blue to black )

    solidRed.rgb /=  1.0 + (-1.0 * pixel.y); // B ( divide ; white to black )

    fragColor = solidRed;
    //fragColor.rgb *= solidRed.bgr;
}
