const float PI = 3.14159;
const float TIME_STEP = 1.0;

const vec3 BLUE = vec3(0.0, 0.4, 1.0);
const vec3 PINK = vec3(1.0, 0.2, 0.6);
const vec3 BACK = vec3(0.1, 0.25, 0.1);

float rand(const float timeShift, const float index)
{
    float f = floor((iTime + timeShift) / TIME_STEP);
    vec2 v = vec2(mod(f + index, iResolution.y)  , mod(f - index, iResolution.x));
    return fract(sin(dot(v.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p0 = vec2(rand(0.0, 0.0), rand(0.0, 1.0));
    vec2 p1 = vec2(rand(1.0, 0.0), rand(1.0, 1.0));
    vec2 p2 = vec2(rand(2.0, 0.0), rand(2.0, 1.0));

    float t = 0.5 + 0.5 * sin(PI * (fract(iTime) - 0.5));

    vec2 p01 = (1.0 - t) * p0 + t * p1;
    vec2 p12 = (1.0 - t) * p1 + t * p2;

	vec2 p = fragCoord.xy / iResolution.xy;

	float h = smoothstep(0.04, 0.025, length(p - p01));
    float g = smoothstep(0.03, 0.015, length(p - p12));

    vec3 v = BLUE * h + PINK * g + BACK * (1.0 - clamp(h - g, 0.0, 1.0)) ;

    fragColor = vec4(v,1.0);
}
