// t âˆˆ [0, 1]
vec4 flag(float p) {
    p = abs(2.*p-1.);
	if (p < 1./5.) {
		return vec4(1);
	} else if (p < 3./5.) {
		return vec4(0.960784, 0.662745, 0.721569, 1);
	} else {
		return vec4(0.356863, 0.807843, 0.980392, 1);
	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = flag(uv.y);
}
