//#define VIEW_POSITION_BUFFER

#define NUM_BODIES 2048

#define BODY_RADIUS 1.4

vec2 invres = vec2(0);

vec4 getBody(int id)
{
    vec2 uv = vec2(0);
    float fid = float(id) * invres.x;

    uv.x = fract(fid);
    uv.y = floor(fid) * invres.y;

    return textureLod(iChannel0, uv, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    invres = 1.0/iResolution.xy;
    vec2 res = iResolution.xy / iResolution.y;
	vec2 uv = fragCoord.xy / iResolution.y;

    float d = 1e6;

    vec2 body = vec2(0);
    for(int i = 0;i < NUM_BODIES;i += 4)
    {
        body = getBody(i + 0).xy - uv;
		d = min(d, dot(body,body));

        body = getBody(i + 1).xy - uv;
		d = min(d, dot(body,body));

        body = getBody(i + 2).xy - uv;
		d = min(d, dot(body,body));

        body = getBody(i + 3).xy - uv;
		d = min(d, dot(body,body));
    }

    d = sqrt(d);

    float px = 1.0/iResolution.y;

    float c = 1.-smoothstep(BODY_RADIUS*px - px, BODY_RADIUS*px, d);

	fragColor = vec4(vec3(c),1.0);

    #ifdef VIEW_POSITION_BUFFER
    fragColor = texture(iChannel0, uv / res / vec2(1.0,16.0));
    #endif
}

//Calculates the acceleration of each object and integrates its position.

#define CLAMP_VIEW

#define NUM_BODIES 2048

#define BODY_MASS 0.1
#define TIME_SCALE 0.001
#define MAX_ACCEL 1e4

vec2 invres = vec2(0);

mat2 Rotate(float a)
{
	return mat2(cos(a), sin(a),-sin(a), cos(a));   
}

//Get a body from the backbuffer with its ID (xy = Current pos, zw = Last pos).
vec4 getBody(int id)
{
    vec2 uv = vec2(0);
    float fid = float(id) * invres.x;

    uv.x = fract(fid);
    uv.y = floor(fid) * invres.y;

    return textureLod(iChannel0, uv, 0.0);
}

//Integrate the body's position.
vec4 Integrate(vec4 body, vec2 accel, float delta)
{
    return vec4(2.0*body.xy - body.zw + accel * delta*delta, body.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    invres = 1.0/iResolution.xy;
    fragCoord = floor(fragCoord);
    int id = int(fragCoord.x) + int(iResolution.x * floor(fragCoord.y));

    if(id >= NUM_BODIES)
    {
        fragColor = vec4(0);
        return;
    }

    vec2 res = iResolution.xy / iResolution.y;

    vec4 body = getBody(id);

    vec2 acc = vec2(0);

    for(int i = 0;i < NUM_BODIES;i++)
    {
            vec2 diff = getBody(i).xy - body.xy;
            acc += (i == id) ? vec2(0) : min(MAX_ACCEL, BODY_MASS / dot(diff, diff)) * normalize(diff);
    }

    body = Integrate(body, acc, iTimeDelta * TIME_SCALE);

    //Initialization (iFrame == 0 doesn't seem to work when the page is initially loaded)
    if(iFrame == 0 || iTime < 2.0)
    {
        vec2 rpos = vec2(0);
        float stride = iChannelResolution[1].x;
        rpos.x = mod(float(id), stride);
        rpos.y = floor(float(id) / stride);
        vec4 rnd = texture(iChannel1, rpos / iChannelResolution[1].xy, -99.0);
        body = abs(rnd.xyxy) * res.xyxy;
        body += sin(float(id)*0.0005)*0.001;
        body.zw += (rnd.zw - 0.5) * 0.001;

        body.zw -= res/2.0;
        body.zw *= Rotate(0.001);
        body.zw = 1.002*body.zw + res/2.0; 
    }

    #ifdef CLAMP_VIEW
    	body.xy = clamp(body.xy, vec2(0), res.xy);
    #endif

    fragColor = body;
}
