float pi = 3.1415926535;

float getYA(float x) {
    return 0.5*iResolution.y+sin((x/30.)+(20.*iTime))*(20.);
}

float getYB(float x) {
    return 0.8*0.55*iResolution.y+sin((x/30.)+(10.*iTime))*(10.) + 0.2*getYA(x*3.-4.);
}

float getYC(float x) {
    return 0.2*0.45*iResolution.y+sin((x/10.)+(40.*iTime))*(20.) + 0.8*getYB(x*1.1+9.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float distA = sqrt(abs(fragCoord.y - getYA(fragCoord.x)));
    vec4 colA = vec4(1.-distA*0.15, 1.-distA*0.7, 1.-distA*0.7, 1.);

    colA = vec4(clamp(colA.x, 0.0, 1.0), clamp(colA.y, 0.0, 1.0), clamp(colA.z, 0.0, 1.0), clamp(colA.w, 0.0, 1.0));


    float distB = sqrt(abs(fragCoord.y - getYB(fragCoord.x)));
    vec4 colB = vec4(1.-distB*0.7, 1.-distB*0.16, 1.-distB*0.7, 1.);

    colB = vec4(clamp(colB.x, 0.0, 1.0), clamp(colB.y, 0.0, 1.0), clamp(colB.z, 0.0, 1.0), clamp(colB.w, 0.0, 1.0));


    float distC = sqrt(abs(fragCoord.y - getYC(fragCoord.x)));
    vec4 colC = vec4(1.-distC*0.7, 1.-distC*0.7, 1.-distC*0.162, 1.);

    colC = vec4(clamp(colC.x, 0.0, 1.0), clamp(colC.y, 0.0, 1.0), clamp(colC.z, 0.0, 1.0), clamp(colC.w, 0.0, 1.0));

    vec4 col = vec4(colA + colB + colC);

    fragColor = vec4(col);
}
