float m(vec2 p0){
    vec2 p=p0;
    //vec2 h;
    float r= 0.0;
    for( float a=0.;a<256.;a++){
        //h.x= p.x*p.x-p.y*p.y+p0.x;
        //h.y= 2.0*p.x*p.y+p0.y;
        //p= h;
        p= vec2( p.x*p.x-p.y*p.y, 2.0*p.x*p.y ) + p0;

        //if( 8.0 < length(p) )
        if( 64.0 < dot(p,p) )
            break;
        else
        	// r += 1.0;
            r= a;
    }
    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pkt= vec2( -0.5901, 0.63029 );
    float size= 4.0 / pow(2.0,8.0*(1.0+sin(iTime)));

	//vec2 uv = fragCoord.xy / iResolution.xy - vec2( 0.5,0.5 );
    vec2 uv = ( fragCoord.xy - iResolution.xy / 2.0 ) / iResolution.y;

    float r= m(pkt+ size*uv) / 128.0;
    //fragColor = vec4(max(0.0,-1.0+r),max(0.0,-0.5+r),r,1.0);
    fragColor = vec4(  -1.0+r,  -0.5+r,  r,   1.0);
}
