#define MASK_INTENSITY 	10. // the higher, brighter areas appear faster
#define TIME_SLOWDOWN	2. // the higher, the slower

const vec4 colorClear = vec4(0.,0.,0.,0.);
const vec4 colorBlack = vec4(0.,0.,0.,1.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = colorBlack;

    vec2 	texuv		= fragCoord.xy / iResolution.xy;
    vec4 	texColor 	= texture( iChannel0, texuv );
    float	timeCosine	= cos( iTime / TIME_SLOWDOWN ),
    		cosSquared	= timeCosine * timeCosine,
    		brightness 	= length( mix( texColor, colorClear, cosSquared )),
        	alphaFactor	= pow( brightness, MASK_INTENSITY );

    fragColor = vec4( texColor.rgb * clamp( alphaFactor, 0., 1. ), 1. );
}
