void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;
    xy.y = abs(xy.y + 0.5 * sin(iTime) * sin(iTime * 0.05 * xy.y));
    xy.x = abs(xy.x + 0.5 * cos(iTime) * sin(iTime * 0.05 * xy.x));
    vec4 texColor = texture(iChannel0, xy);

    texColor.r *= abs(sin(iTime * 0.5));
    texColor.g *= abs(cos(iTime * 0.4));
    texColor.b *= abs(sin(iTime * 0.3) * cos(iTime));

    fragColor = texColor;
}
