vec2 turbulance(inout vec2 p,float freq, float amp,float scroll)
{
    p.x += sin((p.y + scroll * iTime)  * freq) * amp;
    p.y += sin((p.x + scroll * iTime)  * freq) * amp;
    return p;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 oriUv = uv;

    turbulance(uv,5.,.02, .2);
    turbulance(uv,10.,.04, .2);
    turbulance(uv,50.,.01, .1);
   	turbulance(uv,140.,.002, .05);
   	turbulance(uv,280.,.001, .024);

    uv = pow(uv,vec2(1.6,1.6)) * 1.6;


    vec4 t = texture(iChannel0,uv);
    if(oriUv.x > 0.5)
    {
        fragColor = t;;
    }
    else
    {
    	fragColor = vec4(uv,0,1);    
    }

    // check Ã§a c'est bin pratique ;)

}
