void mainImage( out vec4 fragColor, in vec2 fC )
{
	fC -= iResolution.xy * 0.5;
    float sc = pow(iTime*0.5,2.0);

    int n = int(fC.x*fC.y * sc);

    int r = (n * 256 * 256);
	int g = (n * 256 - r * 256) ;
	int b = (n  - r*256 -g*256*256) ;

	fragColor = vec4(vec3(float(b)/256.0,float(g)/256.0,float(r)/256.0),1.0);
}
