void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0,uv);
}

float rand(float co) { return fract(sin(co*(91.3458)) * 47453.5453); }
float shuffle(float x,float div,float i,float seed){
    float original = x;
    i += seed;
    float from = floor(rand((i)*.42)*div);
    float to = floor(rand(i*.57)*div);
    if(original==from){x=to;}
    if(original==to){x=from;}
    return x;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float seed = .25;
    float time = iTime*4.;
    float div = 30.;
    float a = floor(uv.x*div);
    for(float i=0.;i<40.;i++){
        a = shuffle(a,div,i,seed);
    }
    float at = float(mod(floor(time),div)==a);
	fragColor = vec4(a/(div-1.),at+texture(iChannel0,uv).g,at,1.);
}
